import React, { useState } from 'react';
import { Link } from 'react-router-dom';
function Addqa({ refreshqas }) {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [qa, setqa] = useState('');

  const handleAddqa = () => {
    if (!name || !email || !qa) {
      alert('Please fill out all fields.');
      return;
    }

    fetch('http://localhost/giri/add-qa.php', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: `name=${name}&email=${email}&qa=${qa}`,
    })
      .then((response) => response.json())
      .then((data) => {
        alert(data.message);
        if (data.success) {
          setName('');
          setEmail('');
          setqa('');
          refreshqas();
        }
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  return (
    <div>
      <header>
        <h1>MS in DS Academic Program</h1>
      </header>
      <nav>
      <Link to="/">Home</Link>
      <Link to="/about">About</Link>
      <Link to="/Blog">Blog</Link>
      <Link to="/ContactUs">Contact Us</Link>
      <Link to="/services">Services</Link>
      <Link to="/">Logout</Link>
    </nav>
      <h2>Add qa</h2>
      <div className="container">
      <label htmlFor="name">Name:</label>
      <input type="text" id="name" value={name} onChange={(e) => setName(e.target.value)} />
      <br />
      <label htmlFor="email">Email:</label>
      <input type="email" id="email" value={email} onChange={(e) => setEmail(e.target.value)} />
      <br />
      <label htmlFor="qa">qa title:</label>
      <input type="text" id="qa" value={qa} onChange={(e) => setqa(e.target.value)} />
      <br />
      <button onClick={handleAddqa}>Add qa</button>
    </div> <footer>
        <p>&copy; Copyright MS in DS Academic Program</p>
      </footer></div>
  );
}

export default Addqa;
