import React, { useState, useEffect } from 'react';
import AddInstructor from './AddInstructor';
import EditInstructor from './EditInstructor';
import { Link } from 'react-router-dom';

// CSS for the grid layout
const gridStyles = {
  display: 'grid',
  gridTemplateColumns: '1fr 1fr', // Two equal-width columns
  gridGap: '20px', // Adjust the gap between columns as needed
};

function ManageInstructors() {
  const [instructors, setInstructors] = useState([]);

  useEffect(() => {
    // Fetch the list of instructors to populate the list
    fetch('https://gxk1906.uta.cloud/php/retrieve-instructors.php')
      .then((response) => response.json())
      .then((data) => {
        setInstructors(data);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  }, []);

  const refreshInstructors = () => {
    // Function to refresh the instructors list after adding or editing
    fetch('http://localhost/giri/retrieve-instructors.php')
      .then((response) => response.json())
      .then((data) => {
        setInstructors(data);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  return (
    <div>
      <header>
        <h1>MS in DS Academic Program</h1>
      </header>
      <nav>
        <Link to="/">Home</Link>
        <Link to="/about">About</Link>
        <Link to="/Blog">Blog</Link>
        <Link to="/ContactUs">Contact Us</Link>
        <Link to="/services">Services</Link>
        <Link to="/">Logout</Link>
      </nav>
      <div className="container">
        <center>
          <h1>Manage Instructors</h1>
        </center>

        <div style={gridStyles}>
          <AddInstructor refreshInstructors={refreshInstructors} />
          <EditInstructor instructors={instructors} refreshInstructors={refreshInstructors} />
        </div>

        {/* Render the list of instructors */}
        <ul>
          {instructors.map((instructor) => (
            <li key={instructor.id}>
              {instructor.name} - {instructor.email} - {instructor.department}
            </li>
          ))}
        </ul>
      </div>
      <footer>
        <p>&copy; Copyright MS in DS Academic Program</p>
      </footer>
    </div>
  );
}

export default ManageInstructors;
