import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function InstructorDashboard() {
  const [chatMessages, setChatMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  const handleQuickSend = () => {
    if (newMessage.trim() === '') return;
    const updatedChatMessages = [...chatMessages, newMessage];
    setChatMessages(updatedChatMessages);
    setNewMessage('');
  };

  const handleCourseCreation = () => {
    // Add your logic for Course Creation here
    alert('Course Creation clicked');
  };

  const handleCreateExam = () => {
    // Add your logic for creating an exam here
    alert('Create Exam clicked');
  };

  const handleGradePeers = () => {
    // Add your logic for grading peers here
    alert('Grade Peers clicked');
  };

  const handleStudentClick = (studentName) => {
    // Add your logic for handling student button click
    alert(`Clicked on ${studentName}`);
  };

  const handleExamClick = (examName) => {
    // Add your logic for handling exam button click
    alert(`Clicked on ${examName}`);
  };

  return (
    <div>
      <header>
        <h1>MS in DS Academic Program</h1>
      </header>
      <nav>
        <Link to="/Files-i">Files</Link>
        <Link to="/Messages-i">Messages</Link>
        <Link to="/Appointments-i">Appointments</Link>
        <Link to="/student-instructor">Students</Link>
        <Link to="/index">Logout</Link>
      </nav>
      <div className="container">
        <div className="content">
          <div className="dashboard">
            <div className="section">
              <h2>Instructor</h2>
              <button onClick={handleCourseCreation}>Course Creation</button>
              <button onClick={handleCreateExam}>Create Exam</button>
              <button onClick={handleGradePeers}>Grade Peers</button>
            </div>
            <div className="section">
              <h2>Students</h2>
              <button onClick={() => handleStudentClick('Student 1')}>Student 1</button>
              <button onClick={() => handleStudentClick('Student 2')}>Student 2</button>
              <button onClick={() => handleStudentClick('Student 3')}>Student 3</button>
              <button onClick={() => handleStudentClick('Student 4')}>Student 4</button>
            </div>
            <div className="section">
              <h2>Exams</h2>
              <button onClick={() => handleExamClick('Exam 1')}>Exam 1</button>
              <button onClick={() => handleExamClick('Exam 2')}>Exam 2</button>
              <button onClick={() => handleExamClick('Exam 3')}>Exam 3</button>
              <button onClick={() => handleExamClick('Exam 4')}>Exam 4</button>
            </div>
          </div>
        </div>
        <div className="chat-box">
          <div className="close-button">X</div>
          <div className="chat-title">Quick Send</div>
          <div className="chat-content">
            {chatMessages.map((message, index) => (
              <div key={index}>{message}</div>
            ))}
          </div>
          <input
            type="text"
            className="chat-input"
            placeholder="Drop..."
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
          />
          <button className="chat-button" onClick={handleQuickSend}>
            SEND
          </button>
          <div className="chat-content">
            <p>Send a notification</p>
            <p>Send a schedule</p>
          </div>
        </div>
      </div>
      <footer>
        <p>@ copyright MS in DS Academic Program</p>
      </footer>
    </div>
  );
}

export default InstructorDashboard;
