import React, { useState, useEffect } from 'react';
import Addqa from './Addqa';
import Editqa from './Editqa';
import { Link } from 'react-router-dom';

// CSS for the grid layout
const gridStyles = {
  display: 'grid',
  gridTemplateColumns: '1fr 1fr', // Two equal-width columns
  gridGap: '20px', // Adjust the gap between columns as needed
};

function Manageqa() {
  const [qa, setqa] = useState([]);

  useEffect(() => {
    // Fetch the list of qa to populate the list
    fetch('http://localhost/giri/retrieve-qa.php')
      .then((response) => response.json())
      .then((data) => {
        setqa(data);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  }, []);

  return (
    <div>
      <header>
        <h1>MS in DS Academic Program</h1>
      </header>
      <nav>
        <Link to="/">Home</Link>
        <Link to="/about">About</Link>
        <Link to="/Blog">Blog</Link>
        <Link to="/ContactUs">Contact Us</Link>
        <Link to="/services">Services</Link>
        <Link to="/">Logout</Link>
      </nav>
      <div className="container">
        <center>
          <h1>Manage qa</h1>
        </center>

        <div style={gridStyles}>
          <Addqa
            refreshqa={() => {
              // Implement a function to refresh the qa list after adding or editing
              fetch('http://localhost/giri/retrieve-qa.php')
                .then((response) => response.json())
                .then((data) => {
                  setqa(data);
                })
                .catch((error) => {
                  console.error('Error:', error);
                });
            }}
          />

          <Editqa
            qa={qa}
            refreshqa={() => {
              // Implement a function to refresh the qa list after adding or editing
              fetch('http://localhost/giri/retrieve-qa.php')
                .then((response) => response.json())
                .then((data) => {
                  setqa(data);
                })
                .catch((error) => {
                  console.error('Error:', error);
                });
            }}
          />
        </div>

        {/* Render the list of qa */}
        <ul>
          {qa ? (
            qa.map((qa) => (
              <li key={qa.id}>
                {qa.name} - {qa.email} - {qa.qa}
              </li>
            ))
          ) : (
            <li>No qa available</li>
          )}
        </ul>
      </div>
      <footer>
        <p>&copy; Copyright MS in DS Academic Program</p>
      </footer>
    </div>
  );
}

export default Manageqa;
