import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function InstructorDashboard() {
  const [chatMessages, setChatMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  const chatbotResponses = {
    'hi': 'Hello!',
    'how are you?': 'I am just a chatbot. How can I help you today?',
    'course creation': 'You can create a new course from the Course Creation section.',
    'create exam': 'You can create an exam in the Create Exam section.',
    'grade peers': 'You can grade your students\' peers in the Grade Peers section.',
    'students': 'You can access student profiles from the Students section.',
    'exams': 'You can view exam details from the Exams section.',
    'student 1': 'Viewing Student 1 profile...',
    'student 2': 'Viewing Student 2 profile...',
    'student 3': 'Viewing Student 3 profile...',
    'student 4': 'Viewing Student 4 profile...',
    'exam 1': 'Viewing Exam 1 details...',
    'exam 2': 'Viewing Exam 2 details...',
    'exam 3': 'Viewing Exam 3 details...',
    'exam 4': 'Viewing Exam 4 details...',
    'clear chat': 'Chat cleared.',
  };

  const handleQuickSend = () => {
    if (newMessage.trim() === '') return;
    const updatedChatMessages = [...chatMessages, newMessage];
    setChatMessages(updatedChatMessages);
    setNewMessage('');
  };

  const handleChatInputChange = (event) => {
    setNewMessage(event.target.value);
  };

  const handleSendChatMessage = () => {
    if (newMessage) {
      const message = newMessage.toLowerCase();
      if (chatbotResponses[message]) {
        setChatMessages([...chatMessages, message]);
        const response = chatbotResponses[message];
        setChatMessages([...chatMessages, response]);
      } else {
        setChatMessages([...chatMessages, "I don't understand your question."]);
      }
      setNewMessage('');
    }
  };

  return (
    <div>
      <header>
        <h1>MS in DS Academic Program</h1>
      </header>
      <nav>
        <Link to="/Files-i">Files</Link>
        <Link to="/Messages-i">Messages</Link>
        <Link to="/Appointments-i">Appointments</Link>
        <Link to="/student-instructor">Students</Link>
        <Link to="/index">Logout</Link>
      </nav>
      <div className="container">
        <div className="content">
          <div className="dashboard">
            <div className="section">
              <h2>Instructor</h2>
              <button onClick={handleCourseCreation}>Course Creation</button>
              <button onClick={handleCreateExam}>Create Exam</button>
              <button onClick={handleGradePeers}>Grade Peers</button>
            </div>
            <div className="section">
              <h2>Students</h2>
              <Link to="/student-profile">Student 1</Link>
              <Link to="/student-profile">Student 2</Link>
              <Link to="/student-profile">Student 3</Link>
              <Link to="/student-profile">Student 4</Link>
            </div>
            <div className="section">
              <h2>Exams</h2>
              <Link to="/exam-details">Exam 1</Link>
              <Link to="/exam-details">Exam 2</Link>
              <Link to="/exam-details">Exam 3</Link>
              <Link to="/exam-details">Exam 4</Link>
            </div>
          </div>
        </div>
        <div className="chat-box">
          <div className="close-button">X</div>
          <div className="chat-title">Quick Send</div>
          <div className="chat-content">
            {chatMessages.map((message, index) => (
              <div key={index}>{message}</div>
            ))}
          </div>
          <input
            type="text"
            className="chat-input"
            placeholder="Drop..."
            value={newMessage}
            onChange={handleChatInputChange}
          />
          <button className="chat-button" onClick={handleQuickSend}>
            SEND
          </button>
          <div className="chat-content">
            <p>Send a notification</p>
            <p>Send a schedule</p>
          </div>
        </div>
      </div>
      <footer>
        <p>@ copyright MS in DS Academic Program</p>
      </footer>
    </div>
  );
}

export default InstructorDashboard;
