import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './styles.css';

function ContactUs() {
  const [email, setEmail] = useState('');
  const [query, setQuery] = useState('');
  const [formMessage, setFormMessage] = useState('');

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    if (!email || !query) {
      setFormMessage('Please fill in both email and query fields.');
    } else {
      setFormMessage('Submitting...');

      try {
        const response = await fetch('http://localhost/giri/contact.php', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ email, query }),
        });

        if (!response.ok) {
          throw new Error('Network response was not ok');
        }

        const data = await response.json();

        if (data.status === 'success') {
          setFormMessage('Form submitted successfully!');
          setEmail('');
          setQuery('');
        } else {
          setFormMessage(data.message);
        }
      } catch (error) {
        setFormMessage('An error occurred: ' + error.message);
      }
    }
  };

  return (
    <div>
      <header>
        <h1>MS in DS Academic Program</h1>
      </header>
      <nav>
        <Link to="/">Home</Link>
        <Link to="/about">About</Link>
        <Link to="/Blog">Blog</Link>
        <Link to="/contactus">Contact Us</Link>
        <Link to="/services">Services</Link>
        <Link to="/login">Login</Link>
      </nav>
      <div className="container center">
        <center><h2 className="help-heading">CONTACT US</h2></center>
        <div className="form-box">
          <form onSubmit={handleFormSubmit}>
            {formMessage && <div className="form-message">{formMessage}</div>}

            <label htmlFor="email">EMAIL:</label>
            <input
              type="text"
              id="email"
              name="email"
              required
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />

            <label htmlFor="query">QUESTIONS:</label><br></br>
            <textarea
              id="query"
              name="query"
              rows="4"
              cols="50"
              required
              value={query}
              onChange={(e) => setQuery(e.target.value)}
            />
            <br></br><br></br>
            <button className="send-button" type="submit">SEND</button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default ContactUs;
