import React, { useState } from 'react';
import { Link } from 'react-router-dom';
function AddInstructor({ refreshInstructors }) {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [department, setDepartment] = useState('');

  const handleAddInstructor = () => {
    if (!name || !email || !department) {
      alert('Please fill out all fields.');
      return;
    }

    fetch('http://localhost/giri/add-instructor.php', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: `name=${name}&email=${email}&department=${department}`,
    })
      .then((response) => response.json())
      .then((data) => {
        alert(data.message);
        if (data.success) {
          setName('');
          setEmail('');
          setDepartment('');
          refreshInstructors();
        }
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  return (
    <div>
       <header>
        <h1>MS in DS Academic Program</h1>
      </header>
      <nav>
      <Link to="/">Home</Link>
      <Link to="/about">About</Link>
      <Link to="/Blog">Blog</Link>
      <Link to="/ContactUs">Contact Us</Link>
      <Link to="/services">Services</Link>
      <Link to="/login">Login</Link>
    </nav>
    <div className="container">
        <div className="content">
          <div className="dashboard">
      <h2>Add Instructor</h2>
      <div className="container">
      <label htmlFor="name">Name:</label>
      <input type="text" id="name" value={name} onChange={(e) => setName(e.target.value)} />
      <br />
      <label htmlFor="email">Email:</label>
      <input type="email" id="email" value={email} onChange={(e) => setEmail(e.target.value)} />
      <br />
      <label htmlFor="department">Department:</label>
      <input type="text" id="department" value={department} onChange={(e) => setDepartment(e.target.value)} />
      <br />
      <button onClick={handleAddInstructor}>Add Instructor</button>
    </div>
    </div></div></div>
    <footer>
        <p>&copy; Copyright MS in DS Academic Program</p>
      </footer>
      </div>
  );
}

export default AddInstructor;
