import React, { useState } from 'react';

function ExamCreation() {
  const [examName, setExamName] = useState('');
  const [selectedCourse, setSelectedCourse] = useState('');
  const [exams, setExams] = useState([]);
  const [courses, setCourses] = useState(['Course A', 'Course B', 'Course C']); // Sample set of courses

  const handleCreateExam = () => {
    if (examName && selectedCourse) {
      setExams([...exams, { course: selectedCourse, name: examName }]);
      setExamName('');
      setSelectedCourse('');
    }
  };

  return (
    <div>
      <center><h2>Create Exams</h2>
      </center>
      <div class="container">
      <div>
        <input
          type="text"
          placeholder="Enter exam name"
          value={examName}
          onChange={(e) => setExamName(e.target.value)}
        />
        <select
          value={selectedCourse}
          onChange={(e) => setSelectedCourse(e.target.value)}
        >
          <option value="">Select a course</option>
          {courses && courses.length > 0 ? (
            courses.map((course, index) => (
              <option key={index} value={course}>
                {course}
              </option>
            ))
          ) : (
            <option value="">No courses available</option>
          )}
        </select>
        <button onClick={handleCreateExam}>Create Exam</button>
      </div>
      <h3>Created Exams:</h3>
      <table>
        <thead>
          <tr>
            <th>Course</th>
            <th>Exam Name</th>
          </tr>
        </thead>
        <tbody>
          {exams.map((exam, index) => (
            <tr key={index}>
              <td>{exam.course}</td>
              <td>{exam.name}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div></div>
  );
}

export default ExamCreation;
