import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Help() {
  const [email, setEmail] = useState('');
  const [query, setQuery] = useState('');
  const [formMessage, setFormMessage] = useState('');

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handleQueryChange = (e) => {
    setQuery(e.target.value);
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    if (!email || !query) {
      setFormMessage('Please fill in both email and query fields.');
    } else {
      setFormMessage('Submitting...');

      try {
        const response = await fetch('http://localhost/giri/contact.php', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ email, query }),
        });

        if (!response.ok) {
          throw new Error('Network response was not ok');
        }

        const data = await response.json();

        if (data.status === 'success') {
          setFormMessage('Form submitted successfully!');
          setEmail('');
          setQuery('');
        } else {
          setFormMessage(data.message);
        }
      } catch (error) {
        setFormMessage('An error occurred: ' + error.message);
      }
    }
  };

  return (
    <div>
      <header>
        <h1>MS in DS Academic Program</h1>
      </header>
      <nav>
        <Link to="/">Home</Link>
        <Link to="/about">About</Link>
        <Link to="/Blog">Blog</Link>
        <Link to="/contactus">Contact Us</Link>
        <Link to="/services">Services</Link>
        <Link to="/login">Login</Link>
      </nav>
      <div className="container">
        <div className="content">
          <div className="text">
            <center>
              <h2>CONTACT US</h2>
            </center>
            <div className="login-box">
              <div className="box-heading">Email:</div>
              <input
                type="email"
                className="box-input"
                placeholder="Enter your email"
                value={email}
                onChange={handleEmailChange}
              />
              <div className="box-heading">Your Questions:</div>
              <input
                type="text"
                className="box-input"
                placeholder="Enter your questions"
                value={query}
                onChange={handleQueryChange}
              />
              <div className="box-send">
                <button onClick={handleFormSubmit}>SEND</button>
              </div>
            </div>
            <div className="form-message">{formMessage}</div>
          </div>
        </div>
      </div>
      <footer>
        <p>&copy; Copyright MS in DS Academic Program</p>
      </footer>
    </div>
  );
}

export default Help;
