import React, { useState, useEffect } from 'react';
import AddStudent from './AddStudent';
import EditStudent from './EditStudent';
import { Link } from 'react-router-dom';
function ManageStudents() {
  const [students, setStudents] = useState([]);


  useEffect(() => {
    // Fetch the list of Students to populate the list
    fetch('http://localhost/giri/retrieve-students.php')
      .then((response) => response.json())
      .then((data) => {
        setStudents(data);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  }, []);

  return (
    <div>
      <header>
        <h1>MS in DS Academic Program</h1>
      </header>
      <nav>
        <Link to="/">Home</Link>
        <Link to="/about">About</Link>
        <Link to="/Blog">Blog</Link>
        <Link to="/ContactUs">Contact Us</Link>
        <Link to="/services">Services</Link>
        <Link to="/">Logout</Link>
      </nav>
      <div className="container">
      <center><h1>Manage Students</h1></center>

      <AddStudent refreshStudents={() => {
        // Implement a function to refresh the Students list after adding or editing
        fetch('http://localhost/giri/retrieve-students.php')
          .then((response) => response.json())
          .then((data) => {
            setStudents(data);
          })
          .catch((error) => {
            console.error('Error:', error);
          });
      }} />

      <EditStudent students={students} refreshStudents={() => {
        // Implement a function to refresh the Students list after adding or editing
        fetch('http://localhost/giri/retrieve-students.php')
          .then((response) => response.json())
          .then((data) => {
            setStudents(data);
          })
          .catch((error) => {
            console.error('Error:', error);
          });
      }} />

      {/* Render the list of Students */}
      <ul>
  {students ? (
    students.map((student) => (
      <li key={student.id}>
        {student.name} - {student.email} - {student.student}
      </li>
    ))
  ) : (
    <li>No students available</li>
  )}
</ul></div>
<footer>
        <p>&copy; Copyright MS in DS Academic Program</p>
      </footer>
    </div>
  );
}

export default ManageStudents;
