import React, { useState } from 'react';
import { Link } from 'react-router-dom';
function AddCourse({ refreshCourses }) {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [course, setCourse] = useState('');

  const handleAddCourse = () => {
    if (!name || !email || !course) {
      alert('Please fill out all fields.');
      return;
    }

    fetch('http://localhost/giri/add-course.php', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: `name=${name}&email=${email}&course=${course}`,
    })
      .then((response) => response.json())
      .then((data) => {
        alert(data.message);
        if (data.success) {
          setName('');
          setEmail('');
          setCourse('');
          refreshCourses();
        }
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  return (
    <div>
       <header>
        <h1>MS in DS Academic Program</h1>
      </header>
      <nav>
      <Link to="/">Home</Link>
      <Link to="/about">About</Link>
      <Link to="/Blog">Blog</Link>
      <Link to="/ContactUs">Contact Us</Link>
      <Link to="/services">Services</Link>
      <Link to="/login">Login</Link>
    </nav>
      <h2>Add Course</h2>
      <div className="container">
      <label htmlFor="name">Name:</label>
      <input type="text" id="name" value={name} onChange={(e) => setName(e.target.value)} />
      <br />
      <label htmlFor="email">Email:</label>
      <input type="email" id="email" value={email} onChange={(e) => setEmail(e.target.value)} />
      <br />
      <label htmlFor="course">Course:</label>
      <input type="text" id="course" value={course} onChange={(e) => setCourse(e.target.value)} />
      <br />
      <button onClick={handleAddCourse}>Add Course</button>
    </div></div>
  );
}

export default AddCourse;
