import React, { useState } from 'react';
import './styles.css';
import { Link } from 'react-router-dom';

function Signup() {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: '',
    role: 'role1',
    fullName: '',
    birthdate: '',
  });

  const [passwordError, setPasswordError] = useState('');
  const [registrationStatus, setRegistrationStatus] = useState('');

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    if (formData.password !== formData.confirmPassword) {
      setPasswordError("Passwords do not match");
    } else if (
      formData.password.length < 8 ||
      !/[A-Z]/.test(formData.password) ||
      !/[a-z]/.test(formData.password) ||
      !/\d/.test(formData.password) ||
      !/[$&+,:;=?@#|'<>.^*()%!-]/.test(formData.password)
    ) {
      setPasswordError('Password must contain at least 8 characters, including an uppercase letter, a lowercase letter, a number, and a special character ($&+,:;=?@#|\'<>.^*()%!-)');
    } else {
      setPasswordError('');

      try {
        // Your API call for registration here

        // Assuming it's successful, set the registration status
        setRegistrationStatus('Registration Successful');
      } catch (error) {
        setRegistrationStatus('An error occurred. Please try again later.');
      }
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  return (
    <div className="container">
      <div className="content">
        <div className="login-form">
          <h2 className="login-heading">SIGNUP</h2>
          <div>
            <p>
              If you already have an account,{' '}
              <Link to="/login">login here</Link>
            </p>
          </div>
          <form onSubmit={handleFormSubmit} method="post">
            {/* Form fields and validation here */}
          </form>
        </div>
      </div>
    </div>
  );
}

export default Signup;
