import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const QADashboard = () => {
  const [chatMessages, setChatMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  const handleButtonClick = (buttonName) => {
    // Add functionality for the buttons here
    alert(`Button Clicked: ${buttonName}`);
  };

  const handleChatInputChange = (event) => {
    setNewMessage(event.target.value);
  };

  const handleSendChatMessage = () => {
    if (newMessage) {
      setChatMessages([...chatMessages, newMessage]);
      setNewMessage('');
    }
  };

  return (
    <div>
      <header>
        <h1>MS in DS Academic Program</h1>
      </header>
      <nav>
        <Link to="/student-qa">Student</Link>
        <Link to="/Messages-qa">Message</Link>
        <Link to="/instructor-qa">Instructor</Link>
        <Link to="/progcord-qa">Coordinator</Link>
        <Link to="/">Logout</Link>
      </nav>
      <div className="container">
        <div className="content">
          <div className="dashboard">
            <div className="section">
              <h2>QA Officer</h2>
              <button onClick={() => handleButtonClick('Reports')}>Reports</button>
              <button onClick={() => handleButtonClick('Program Review')}>Program Review</button>
              <button onClick={() => handleButtonClick('Student Support')}>Student Support</button>
              <button onClick={() => handleButtonClick('Feedback Tools')}>Feedback Tools</button>
              <button onClick={() => handleButtonClick('Assessment Improvement')}>Assessment Improvement</button>
              <button onClick={() => handleButtonClick('Faculty Development')}>Faculty Development</button>
              <button onClick={() => handleButtonClick('Quality Standards')}>Quality Standards</button>
            </div>
          </div>
        </div>
        <div className="chat-box">
          <div className="close-button">X</div>
          <div className="chat-title">Welcome to the Chatbox!!</div>
          <div className="chat-content">
            {chatMessages.map((message, index) => (
              <div key={index}>{message}</div>
            ))}
          </div>
          <input
            type="text"
            className="chat-input"
            placeholder="Type here"
            value={newMessage}
            onChange={handleChatInputChange}
          />
          <button className="chat-button" onClick={handleSendChatMessage}>
            SEND
          </button>
        </div>
      </div>
      <footer>
        <p>@ copyright MS in DS Academic Program</p>
      </footer>
    </div>
  );
};

export default QADashboard;
